    }

    

    function pengeluaranPerCategory()
    {
        $id_pengguna = $this->session->userdata("id");

        $query = "
            SELECT 
                c.id, c.nama, sum(t.nominal) as totalPemakaian
            FROM 
                `transaksi` t 
                inner join category c on c.id = t.id_category
             where 
                c.jenis = 0
                and t.id_pengguna = $id_pengguna
             GROUP by c.id
        ";

        return $this->tampilData($query);

    }

    function PemasukanPerCategory()
    {
        $id_pengguna = $this->session->userdata("id");

        $query = "
            SELECT 
                c.id, c.nama, sum(t.nominal) as totalPemakaian
            FROM 
                `transaksi` t 
                inner join category c on c.id = t.id_category
             where 
                c.jenis = 1
                and t.id_pengguna = $id_pengguna
             GROUP by c.id
        ";

        return $this->tampilData($query);
    }
